/*Schema A creation + Table creation */
CREATE SCHEMA [SchemaA]
GO
CREATE TABLE [SchemaA].[Employee]
(
    EmployeeKey INT,
    EmployeeName VARCHAR(30),
    EmployeeRole VARCHAR(20),
    Salary INT
);
GO

INSERT INTO [SchemaA].[Employee] (EmployeeKey, EmployeeName, EmployeeRole, Salary)
VALUES (1, 'EmployeeA', 'Engineer', 100000);
INSERT INTO [SchemaA].[Employee] (EmployeeKey, EmployeeName, EmployeeRole, Salary)
VALUES (2, 'EmployeeB', 'Sales', 90000);
INSERT INTO [SchemaA].[Employee] (EmployeeKey, EmployeeName, EmployeeRole, Salary)
VALUES (3, 'EmployeeC', 'Manager', 200000);


/*Schema B creation + View creation */
CREATE SCHEMA [SchemaB]
GO
CREATE VIEW [SchemaB].[ViewNoSalary]
AS
SELECT
a.EmployeeKey,
a.EmployeeName,
a.EmployeeRole
FROM [SchemaA].[Employee] a;

/* AD Authentication */
CREATE USER [sbahadur@microsoft.com] FROM EXTERNAL PROVIDER;

GRANT SELECT ON OBJECT::[SchemaB].[ViewNoSalary] TO [sbahadur@microsoft.com]
GO

EXECUTE AS USER = 'sbahadur@microsoft.com';
GO
SELECT * FROM [SchemaB].[ViewNoSalary];

EXECUTE AS USER = 'sbahadur@microsoft.com';
GO
SELECT * FROM [SchemaA].[Employee];


/* SQL Users Authentication */
CREATE USER TestProcess WITHOUT LOGIN;

GRANT SELECT ON OBJECT::[SchemaB].[ViewNoSalary] TO TestProcess
GO

EXECUTE AS USER = 'TestProcess';
GO
SELECT * FROM [SchemaB].[ViewNoSalary];

EXECUTE AS USER = 'TestProcess';
GO
SELECT * FROM [SchemaA].[Employee];
